import React from "react";
const UserInput = () => {
    useState();
    const submitHandler = (event) => {
        event.preventDefault();  //Browsersdefault behavior is stopped(Reloding after submit)
        alert('SUBMIT');
    };
    const resetHandler = (event) => {
        alert('RESET');
    };
    const inputChangeHandler = (input, value) => {
        console.log(input, value);
    };
  return (
    <form onSubmit={submitHandler} className="form">
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input onChange={(event) => 
            inputChangeHandler('current-savings', event.target.value )} type="number" id="current-savings" />  {/*inputChangeHandler function will not be executed when this line of code is parsed, but instead only when this arrow function will be executed. And this function will be excuted only by React when the value in this input changes. 'current-savings'=input & event.target.value=value */}
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input onChange={(event) => 
            inputChangeHandler('yearly-contribution', event.target.value )} type="number" id="yearly-contribution" />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input onChange={(event) => 
            inputChangeHandler('expected-return', event.target.value )} type="number" id="expected-return" />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input onChange={(event) => 
            inputChangeHandler('duration', event.target.value )} type="number" id="duration" />
        </p>
      </div>
      <p className="actions">
        <button onClick={resetHandler} type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};
export default UserInput;
