import { useRef, useState } from "react";
const SimpleInput = props => {
  const nameInputRef = useRef(); //Ref
  const [enteredName, setEnteredName] = useState(""); //State
  const nameInputChangeHandler = event => {
    setEnteredName(event.target.value);
  };
  const formSubmissionHandler = event => {
    event.preventDefault();
    console.log(enteredName);
    const enteredValue = nameInputRef.current.value;
    console.log(enteredValue);
    nameInputRef.current.value = ''; //Reset the entered value using refs()
    // setEnteredName(''); //Reset the enetered value using state(Ideal)
  };
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="form-control">
        <label htmlFor="name">Your Name</label>
        <input
          ref={nameInputRef}
          onClick={nameInputChangeHandler}
          type="text"
          id="name"
          value={enteredName}
        />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
